public class App {
    
    static class Member {
        protected String name;
        protected String email;
        protected String password;
        

        @Override
        public String toString() {
            return "Member [name=" + name + ", email=" + email + ", password=" + password + "]";
        }
        
        public String getName() {
            return name;
        }
        public void setName(String name) {
            this.name = name;
        }
        public String getEmail() {
            return email;
        }
        public void setEmail(String email) {
            this.email = email;
        }
        public String getPassword() {
            return password;
        }
        public void setPassword(String password) {
            this.password = password;
        }
        
        
    }
    
    static class Student extends Member {
        String school;
        boolean working;
        String tel;
        
        public String getSchool() {
            return school;
        }
        public void setSchool(String school) {
            this.school = school;
        }
        public boolean isWorking() {
            return working;
        }
        public void setWorking(boolean working) {
            this.working = working;
        }
        public String getTel() {
            return tel;
        }
        public void setTel(String tel) {
            this.tel = tel;
        }
    }
    
    static class Teacher extends Member {
        protected String tel;
        protected int pay;
        protected String subject;
        
        
        public String getTel() {
            return tel;
        }
        public void setTel(String tel) {
            this.tel = tel;
        }
        public int getPay() {
            return pay;
        }
        public void setPay(int pay) {
            this.pay = pay;
        }
        public String getSubject() {
            return subject;
        }
        public void setSubject(String subject) {
            this.subject = subject;
        }
        
        
    }
    
    
        static Student[] students = new Student[100];
        static Teacher[] teachers = new Teacher[100];
        
        static int studentIndex = 0;
        static int teacherIndex = 0;
        
        //1)키보드 입력을 처리할 객체 준비
        static Scanner keyIn = new Scanner(System.in); 
    
    public static void main(String[] args) {
        
        while (true ) {
            String menu = promptMenu();
            if (menu.equals("1")) {
                serviceStudentMenu();
            }else if (menu.equals("1")) {
                serviceTeacherMenu();
            }else {
                System.out.println("사요나라");
                break;
            }
        }
        
        keyIn.close();

        

    }

    private static void serviceStudentMenu() {
        while (true) {
                System.out.println("학생 관리>");
                String command = keyIn.nextLine();
                if (command.equals("list")) {
                    printStudents();
                }else if (command.equals("add")) {
                    inputStudents();
                }else if (command.equals("quit")) {
                    break;
                }else {
                    System.out.println("유효하지 않은 명령입니다");
                }                
        }
    }
    
    private static void serviceTeacherMenu() {
        while (true) {
                System.out.println("강사 관리>");
                String command = keyIn.nextLine();
                if (command.equals("list")) {
                    printStudents();
                }else if (command.equals("add")) {
                    inputStudents();
                }else if (command.equals("quit")) {
                    break;
                }else {
                    System.out.println("유효하지 않은 명령입니다");
                }                
        }
    }

    private static String promptMenu() {
        System.out.println("[메뉴]");
        System.out.println("1. 학생 관리");
        System.out.println("2. 강사 관리");
        System.out.println("3. 매니저 관리");
        System.out.println("0. 종료");
        
        while (true) {
            System.out.print("메뉴 번호> ");
            
            String menu = keyIn.nextLine();
            System.out.println(menu);
            
            switch (menu) {
            case "1":
            case "2":
            case "3":
            case "0":
                return menu;
            default:
                System.out.println("메뉴 번호가 유효하지 않습니다");
            }            
        }
        
//        if (menu.equals("1")) {
//            inputMembers();
//            
//            printMembers();            
//        }else {
//            System.out.println("메뉴 번호가 유효하지 않습니다");
//        }
    }
    
    static void printStudents() {
        int count = 0;
        for (Student s : students) {
            if (count++==studentIndex)
                break;
            System.out.printf("%s, %s, %s\n"
                    ,s.getName()
                    ,s.getEmail()
                    ,s.getPassword()
                    ,s.getSchool()
                    ,s.isWorking()
                    ,s.getTel());
        }
/*        for (int i = 0; i < index; i++)
        System.out.printf("%s, %s, %s\n",members[i].getName(),members[i].getEmail(),members[i].getPassword());
*/
    }
    
    static void printTeacher() {
        int count = 0;
        for (Teacher s : teachers) {
            if (count++==teacherIndex)
                break;
            System.out.printf("%s, %s, %s\n"
                    ,s.getName()
                    ,s.getEmail()
                    ,s.getPassword()
                    ,s.getTel()
                    ,s.getPay()
                    ,s.getSubject());
        }
    }
    
    static void inputStudents() {
        //2) 사용자로부터 회원 정보 입력받기
        while (true) {
            //members[index] = new Member();
            Student m = new Student();
            
            System.out.println("이름?");
            m.setName(keyIn.nextLine());
            
            System.out.println("이메일?");
            m.setEmail(keyIn.nextLine());
            
            System.out.println("암호?");
            m.setPassword(keyIn.nextLine());
            
            System.out.println("최종학력?");
            m.setSchool(keyIn.nextLine());
            
            System.out.println("재직여부?(true/false)");
            m.setWorking(Boolean.parseBoolean(keyIn.nextLine()));
            
            System.out.println("전화번호?");
            m.setTel(keyIn.nextLine());
            
            students[studentIndex++] = m;
            
            //index++;
            
            System.out.println("계속하시겠습니까? (y/n)");
            String answer = keyIn.nextLine();
            if(answer.toLowerCase().equals("n"))
                break;
        }
    }//inputStudent 메서드
    
    static void inputTeachers() {
        //2) 사용자로부터 회원 정보 입력받기
        while (true) {
            //members[index] = new Member();
            Teacher m = new Teacher();
            
            System.out.println("이름?");
            m.setName(keyIn.nextLine());
            
            System.out.println("이메일?");
            m.setEmail(keyIn.nextLine());
            
            System.out.println("암호?");
            m.setPassword(keyIn.nextLine());
            
            System.out.println("전화번호?");
            m.setTel(keyIn.nextLine());
            
            System.out.println("시급?");
            m.setPay(Integer.parseInt(keyIn.nextLine()));
            
            System.out.println("강의과목?(예 : Java,C,C++");
            m.setSubject(keyIn.nextLine());
            
            teachers[teacherIndex++] = m;
            
            //index++;
            
            System.out.println("계속하시겠습니까? (y/n)");
            String answer = keyIn.nextLine();
            if(answer.toLowerCase().equals("n"))
                break;
        }
    }//inputStudent 메서드
}
